INSERT INTO `tf_addonarticle` (`id`,`aid`,`content`,`create_time`,`update_time`) VALUES ('14','12','&lt;span style=&quot;color:#333333;font-family:&amp;quot;Microsoft YaHei&amp;quot;, SimHei;font-size:15.99px;background-color:#FFFFFF;&quot;&gt;人生若只如初见，何事秋风悲画扇。&lt;/span&gt;&lt;br /&gt;
