INSERT INTO `tf_addonarticle` (`id`,`aid`,`content`,`create_time`,`update_time`) VALUES ('36','44','&lt;p&gt;
	在使用thinkphp上传文件时，最好上传到public文件夹下面，因为thinkphp框架会禁止对文件操作。
&lt;/p&gt;
&lt;p&gt;
	上传文件成功后，返回的文件信息需要加上uploads文件夹的父文件夹。如：
&lt;/p&gt;
&lt;p&gt;
	上传文件地址：uploads。实际上传地址为: /thinkphp5/public/uploads
&lt;/p&gt;
&lt;p&gt;
	返回文件信息需要添加: /thinkphp5/public
&lt;/p&gt;
&lt;pre class=&quot;brush:js;&quot;&gt;	
$file = request()-&amp;gt;file('imgFile');//获取信息
if ($file){
    $info = $file-&amp;gt;validate()-&amp;gt;move($this-&amp;gt;file_move_path);//存储到指定位置
    $file_path = $this-&amp;gt;file_back_path.DS.$info-&amp;gt;getSaveName();//获取返回信息
    $file_path = '/Tp5-TuFanInc-Admin-master/public'.$up_config['image_url']
        .str_replace('\\', '/', $file_path);//完整路径
    echo json_encode(['error' =&amp;gt; 0, 'url' =&amp;gt; $file_path]);//JSON返回客户端
&lt;/pre&gt;','1519722871','1519723637');
