INSERT INTO `tf_addonarticle` (`id`,`aid`,`content`,`create_time`,`update_time`) VALUES ('39','64','&lt;span style=&quot;font-size:18px;&quot;&gt;&amp;nbsp; &amp;nbsp; 什么时候开始喜欢一个人的呢，谁说的出来啊，朦朦胧胧的喜欢，一开始自己也是不知道的。&lt;/span&gt; 
&lt;h2 style=&quot;text-align:center;&quot;&gt;
	&lt;span style=&quot;font-size:18px;&quot;&gt;一个小梦&lt;/span&gt; 
&lt;/h2&gt;
&lt;span style=&quot;font-size:16px;&quot;&gt; 
&lt;div style=&quot;text-align:center;&quot;&gt;
	&lt;span style=&quot;font-size:18px;&quot;&gt;2014.10.27&lt;/span&gt; 
&lt;/div&gt;
&lt;/span&gt; 
&lt;blockquote&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;昨天晚上梦到你了&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;起初梦里面有你  我&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;还有我们高中同学&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;我们有说有笑  后来你走了&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;当你再次出现时&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;是你跟林一起来的&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;那是 开同学会 我也去了&lt;/span&gt;&lt;span style=&quot;font-size:18px;&quot;&gt;&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;中途我与林没有说话&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;本来我很期待你能与我有说有笑&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;但什么也没说&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;别人都跟林喝了酒&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;庆祝你们在一起&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;我没去......&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;聚会结束 你们上了飞机&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;临走之前你只说了一句话 &amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;鼓着你的两个眼睛对我说&amp;nbsp;&lt;/span&gt; 
	&lt;/p&gt;
	&lt;p&gt;
		&lt;span style=&quot;font-size:18px;&quot;&gt;我们还是别联系吧......&amp;nbsp;&lt;/span&gt; 
