--计算1到指定数之间的和

delimiter $$
create function display(int_1 int) returns int 
begin 
	set @i = 1;#@定义的变量时全局变量，在函数外部也可以访问
	set @res = 0;
	while @i &lt;= int_1 do 
	--任何变量修改必须用set
		set @res = @res + @i;
		set @i = @i + 1;
	end while;
	return @res;
end 
$$

