INSERT INTO `tf_addonarticle` (`id`,`aid`,`content`,`create_time`,`update_time`) VALUES ('194','219','&lt;pre&gt;class Solution:
    def subsetsWithDup(self, nums):
        &quot;&quot;&quot;
        :type nums: List[int]
        :rtype: List[List[int]]
        &quot;&quot;&quot;
        nums.sort()
        # print(nums)
        res = [[]]
        l = None
        for i in range(len(nums)):
            if i == 0 or nums[i] != nums[i - 1]:
                l = len(res)
            for j in range(len(res) - l, len(res)):
                res.append(res[j] + [nums[i]])
                print(&quot;loop:&quot;,i,&quot;---&amp;gt;&quot;,res,&quot;&amp;gt;&amp;gt;&amp;gt;&amp;gt;&amp;gt;&amp;gt;&quot;,res[j],[nums[i]],l)
        return res
&lt;/pre&gt;

