INSERT INTO `tf_arc_comment` (`id`,`uid`,`ruid`,`cid`,`aid`,`tid`,`content`,`recontent`,`status`,`create_time`,`update_time`) VALUES ('188','43','0','0','209','0','[root]#&nbsp;sort&nbsp;-M&nbsp;file&nbsp;#排序，常用-nr<br>-b：忽略每行前面开始出的空格字符；<br>-c：检查文件是否已经按照顺序排序；<br>-d：排序时，处理英文字母、数字及空格字符外，忽略其他的字符；<br>-f：排序时，将小写字母视为大写字母；<br>-i：排序时，除了040至176之间的ASCII字符外，忽略其他的字符；<br>-m：将几个排序号的文件进行合并；<br>-M：将前面3个字母依照月份的缩写进行排序；<br>-n：依照数值的大小排序；<br>-o&lt;输出文件&gt;：将排序后的结果存入指定的文件；<br>-r：以相反的顺序来排序；<br>-t&lt;分隔字符&gt;：指定排序时所用的栏位分隔字符；<br>+&lt;起始栏位&gt;-&lt;结束栏位&gt;：以指定的栏位来排序，范围由起始栏位到结束栏位的前一栏位。','','1','1523719978','1523719978');
