  `mid` int(11) NOT NULL COMMENT '大师id',
  `uid` int(11) NOT NULL COMMENT '用户id',
  `order_num` varchar(100) NOT NULL COMMENT '订单编号',
  `order_info` text COMMENT '订单详情',
  `order_amount` decimal(12,2) NOT NULL COMMENT '订单金额',
  `order_step` tinyint(1) NOT NULL DEFAULT '1' COMMENT '订单进度【-2退款订单-1订单过期1待付款2已付款待服务3已服务完成订单待评价4已评价】',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '状态',
  `create_time` int(10) NOT NULL COMMENT '创建时间',
  `update_time` int(10) NOT NULL COMMENT '编辑时间',
  PRIMARY KEY (`id`),
  KEY `mid` (`mid`),
  KEY `uid` (`uid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
-- ----------------------------
-- Records of tf_order
-- ----------------------------

