DROP TABLE IF EXISTS `tf_timeline`;
CREATE TABLE `tf_timeline` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) DEFAULT NULL COMMENT '用户id',
  `ruid` int(11) DEFAULT '0' COMMENT '回复者的id',
  `aid` int(11) NOT NULL DEFAULT '0' COMMENT '浏览文章id',
  `description` varchar(200) DEFAULT NULL COMMENT '摘要',
  `content` varchar(200) DEFAULT NULL COMMENT '内容',
  `icon` varchar(32) DEFAULT NULL COMMENT '标签,time-label时间，fa fa-envelope bg-blue信封,fa fa-user bg-aqua用户，fa fa-comments bg-yellow评论，fa fa-camera bg-purple图片',
  `action` tinyint(2) DEFAULT NULL COMMENT '操作: 0登录，1阅读，2点赞，3文章留言，4文章回复一级留言消息，5添加好友，6添加好友成功，7删除好友成功，8注销成功，9文章回复二级留言，10为为别人对自己文章留言的回复，11注册',
  `link` varchar(200) DEFAULT NULL COMMENT '相关评论或文章的链接',
  `create_time` int(11) DEFAULT NULL COMMENT '创建时间',
  `update_time` int(11) DEFAULT NULL,
  `action_id` int(11) DEFAULT NULL COMMENT '当前操作的id',
  `active` tinyint(1) DEFAULT '0' COMMENT '0默认，对别人文章操作。\n1别人对自己文章操作。',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=827 DEFAULT CHARSET=gbk COMMENT='时间轴';
